// help method
std::vector<std::string> TestMultipleContainers::help(const std::string& functionName)
{
    bool showAll = (functionName == "--all");
    std::vector<std::string> helpString;
    if (showAll) {
        helpString.emplace_back("*** Available commands:");
        helpString.emplace_back(TestMultipleContainers_testList1_helper::s_tag);
        helpString.emplace_back(TestMultipleContainers_testList2_helper::s_tag);
        helpString.emplace_back(TestMultipleContainers_testMap1_helper::s_tag);
        helpString.emplace_back(TestMultipleContainers_testMap2_helper::s_tag);
        helpString.emplace_back(TestMultipleContainers_testSet1_helper::s_tag);
        helpString.emplace_back(TestMultipleContainers_testSet2_helper::s_tag);
        helpString.emplace_back("help");
    } else {
        if (functionName == TestMultipleContainers_testList1_helper::s_tag) {
            helpString.emplace_back(TestMultipleContainers_testList1_helper::s_prototype);
        }
        if (functionName == TestMultipleContainers_testList2_helper::s_tag) {
            helpString.emplace_back(TestMultipleContainers_testList2_helper::s_prototype);
        }
        if (functionName == TestMultipleContainers_testMap1_helper::s_tag) {
            helpString.emplace_back(TestMultipleContainers_testMap1_helper::s_prototype);
        }
        if (functionName == TestMultipleContainers_testMap2_helper::s_tag) {
            helpString.emplace_back(TestMultipleContainers_testMap2_helper::s_prototype);
        }
        if (functionName == TestMultipleContainers_testSet1_helper::s_tag) {
            helpString.emplace_back(TestMultipleContainers_testSet1_helper::s_prototype);
        }
        if (functionName == TestMultipleContainers_testSet2_helper::s_tag) {
            helpString.emplace_back(TestMultipleContainers_testSet2_helper::s_prototype);
        }
        if (functionName == "help") {
            helpString.emplace_back("std::vector<std::string> help(const std::string& functionName = \"--all\")");
            helpString.emplace_back("Return list of available commands, or help message for a specific function");
            helpString.emplace_back("@param functionName name of command for which to get a detailed description. If none or '--all' is provided, print list of available commands");
            helpString.emplace_back("@return list of strings (one string per line)");
        }
    }
    if (helpString.empty()) {
        helpString.emplace_back("Command not found");
    }
    return helpString;
}

// read from ConnectionReader
bool TestMultipleContainers::read(yarp::os::ConnectionReader& connection)
{
    constexpr size_t max_tag_len = 1;
    size_t tag_len = 1;

    yarp::os::idl::WireReader reader(connection);
    reader.expectAccept();
    if (!reader.readListHeader()) {
        reader.fail();
        return false;
    }

    std::string tag = reader.readTag(1);
    bool direct = (tag == "__direct__");
    if (direct) {
        tag = reader.readTag(1);
    }
    while (tag_len <= max_tag_len && !reader.isError()) {
        if(tag == "getRemoteProtocolVersion") {
            if (!reader.noMore()) {
                yError("Reader invalid protocol?! %s:%d - %s", __FILE__, __LINE__, __YFUNCTION__);
                reader.fail();
                return false;
            }

            auto proto = getLocalProtocolVersion();

            yarp::os::idl::WireWriter writer(reader);
           if (!writer.writeListHeader(1)) {
                yWarning("Writer invalid protocol?! %s:%d - %s", __FILE__, __LINE__, __YFUNCTION__);
               return false;}
            if (!writer.write(proto)) {
                yWarning("Writer invalid protocol?! %s:%d - %s", __FILE__, __LINE__, __YFUNCTION__);
                return false;
            }
            reader.accept();
            return true;
        }
        if (tag == TestMultipleContainers_testList1_helper::s_tag) {
            TestMultipleContainers_testList1_helper helper;
            if (!helper.cmd.readArgs(reader)) {
                return false;
            }

            helper.call(this);

            yarp::os::idl::WireWriter writer(reader);
            if (!helper.reply.write(writer)) {
                return false;
            }
            reader.accept();
            return true;
        }
        if (tag == TestMultipleContainers_testList2_helper::s_tag) {
            TestMultipleContainers_testList2_helper helper;
            if (!helper.cmd.readArgs(reader)) {
                return false;
            }

            helper.call(this);

            yarp::os::idl::WireWriter writer(reader);
            if (!helper.reply.write(writer)) {
                return false;
            }
            reader.accept();
            return true;
        }
        if (tag == TestMultipleContainers_testMap1_helper::s_tag) {
            TestMultipleContainers_testMap1_helper helper;
            if (!helper.cmd.readArgs(reader)) {
                return false;
            }

            helper.call(this);

            yarp::os::idl::WireWriter writer(reader);
            if (!helper.reply.write(writer)) {
                return false;
            }
            reader.accept();
            return true;
        }
        if (tag == TestMultipleContainers_testMap2_helper::s_tag) {
            TestMultipleContainers_testMap2_helper helper;
            if (!helper.cmd.readArgs(reader)) {
                return false;
            }

            helper.call(this);

            yarp::os::idl::WireWriter writer(reader);
            if (!helper.reply.write(writer)) {
                return false;
            }
            reader.accept();
            return true;
        }
        if (tag == TestMultipleContainers_testSet1_helper::s_tag) {
            TestMultipleContainers_testSet1_helper helper;
            if (!helper.cmd.readArgs(reader)) {
                return false;
            }

            helper.call(this);

            yarp::os::idl::WireWriter writer(reader);
            if (!helper.reply.write(writer)) {
                return false;
            }
            reader.accept();
            return true;
        }
        if (tag == TestMultipleContainers_testSet2_helper::s_tag) {
            TestMultipleContainers_testSet2_helper helper;
            if (!helper.cmd.readArgs(reader)) {
                return false;
            }

            helper.call(this);

            yarp::os::idl::WireWriter writer(reader);
            if (!helper.reply.write(writer)) {
                return false;
            }
            reader.accept();
            return true;
        }
        if (tag == "help") {
            std::string functionName;
            if (!reader.readString(functionName)) {
                functionName = "--all";
            }
            auto help_strings = help(functionName);
            yarp::os::idl::WireWriter writer(reader);
            if (!writer.isNull()) {
                if (!writer.writeListHeader(2)) {
                    return false;
                }
                if (!writer.writeTag("many", 1, 0)) {
                    return false;
                }
                if (!writer.writeListBegin(0, help_strings.size())) {
                    return false;
                }
                for (const auto& help_string : help_strings) {
                    if (!writer.writeString(help_string)) {
                        return false;
                    }
                }
                if (!writer.writeListEnd()) {
                    return false;
                }
            }
            reader.accept();
            return true;
        }
        if (reader.noMore()) {
            reader.fail();
            return false;
        }
        std::string next_tag = reader.readTag(1);
        if (next_tag.empty()) {
            break;
        }
        tag.append("_").append(next_tag);
        tag_len = std::count(tag.begin(), tag.end(), '_') + 1;
    }
    return false;
}
