WorldInterfaceServer_makeSphere_helper::Command::Command(const double radius, const Pose& pose, const Color& color, const std::string& frame_name, const std::string& object_name, const bool gravity_enable, const bool collision_enable) :
        radius{radius},
        pose{pose},
        color{color},
        frame_name{frame_name},
        object_name{object_name},
        gravity_enable{gravity_enable},
        collision_enable{collision_enable}
{
}

bool WorldInterfaceServer_makeSphere_helper::Command::write(yarp::os::ConnectionWriter& connection) const
{
    yarp::os::idl::WireWriter writer(connection);
    if (!writer.writeListHeader(s_cmd_len)) {
        return false;
    }
    return write(writer);
}

bool WorldInterfaceServer_makeSphere_helper::Command::read(yarp::os::ConnectionReader& connection)
{
    yarp::os::idl::WireReader reader(connection);
    if (!reader.readListHeader()) {
        reader.fail();
        return false;
    }
    return read(reader);
}

bool WorldInterfaceServer_makeSphere_helper::Command::write(const yarp::os::idl::WireWriter& writer) const
{
    if (!writeTag(writer)) {
        return false;
    }
    if (!writeArgs(writer)) {
        return false;
    }
    return true;
}

bool WorldInterfaceServer_makeSphere_helper::Command::writeTag(const yarp::os::idl::WireWriter& writer) const
{
    if (!writer.writeTag(s_tag, 1, s_tag_len)) {
        return false;
    }
    return true;
}

bool WorldInterfaceServer_makeSphere_helper::Command::writeArgs(const yarp::os::idl::WireWriter& writer) const
{
    if (!writer.writeFloat64(radius)) {
        return false;
    }
    if (!writer.write(pose)) {
        return false;
    }
    if (!writer.write(color)) {
        return false;
    }
    if (!writer.writeString(frame_name)) {
        return false;
    }
    if (!writer.writeString(object_name)) {
        return false;
    }
    if (!writer.writeBool(gravity_enable)) {
        return false;
    }
    if (!writer.writeBool(collision_enable)) {
        return false;
    }
    return true;
}

bool WorldInterfaceServer_makeSphere_helper::Command::read(yarp::os::idl::WireReader& reader)
{
    if (!readTag(reader)) {
        return false;
    }
    if (!readArgs(reader)) {
        return false;
    }
    return true;
}

bool WorldInterfaceServer_makeSphere_helper::Command::readTag(yarp::os::idl::WireReader& reader)
{
    std::string tag = reader.readTag(s_tag_len);
    if (reader.isError()) {
        return false;
    }
    if (tag != s_tag) {
        reader.fail();
        return false;
    }
    return true;
}

bool WorldInterfaceServer_makeSphere_helper::Command::readArgs(yarp::os::idl::WireReader& reader)
{
    if (reader.noMore()) {
        reader.fail();
        return false;
    }
    if (!reader.readFloat64(radius)) {
        reader.fail();
        return false;
    }
    if (reader.noMore()) {
        reader.fail();
        return false;
    }
    if (!reader.read(pose)) {
        reader.fail();
        return false;
    }
    if (reader.noMore()) {
        reader.fail();
        return false;
    }
    if (!reader.read(color)) {
        reader.fail();
        return false;
    }
    if (!reader.readString(frame_name)) {
        frame_name = "";
    }
    if (!reader.readString(object_name)) {
        object_name = "";
    }
    if (!reader.readBool(gravity_enable)) {
        gravity_enable = false;
    }
    if (!reader.readBool(collision_enable)) {
        collision_enable = true;
    }
    if (!reader.noMore()) {
        reader.fail();
        return false;
    }
    return true;
}

bool WorldInterfaceServer_makeSphere_helper::Reply::write(yarp::os::ConnectionWriter& connection) const
{
    yarp::os::idl::WireWriter writer(connection);
    return write(writer);
}

bool WorldInterfaceServer_makeSphere_helper::Reply::read(yarp::os::ConnectionReader& connection)
{
    yarp::os::idl::WireReader reader(connection);
    return read(reader);
}

bool WorldInterfaceServer_makeSphere_helper::Reply::write(const yarp::os::idl::WireWriter& writer) const
{
    if (!writer.isNull()) {
        if (!writer.write(return_helper)) {
            return false;
        }
    }
    return true;
}

bool WorldInterfaceServer_makeSphere_helper::Reply::read(yarp::os::idl::WireReader& reader)
{
    if (reader.noMore()) {
        reader.fail();
        return false;
    }
    if (!reader.read(return_helper)) {
        reader.fail();
        return false;
    }
    return true;
}

void WorldInterfaceServer_makeSphere_helper::call(WorldInterfaceServer* ptr)
{
    reply.return_helper = ptr->makeSphere(cmd.radius, cmd.pose, cmd.color, cmd.frame_name, cmd.object_name, cmd.gravity_enable, cmd.collision_enable);
}

